import { Link } from 'react-router-dom'
import { useForm } from 'react-hook-form'

const Contact = () => {

  const { register, handleSubmit, watch, errors } = useForm();
  const onSubmit = data => console.log(data.name);
  const message = watch('message') || "";
  const messageCharsLeft = 1500 - message.length;

  return (
    <div className='contact'>
      <button className='back'><Link to="/">back</Link></button>
      <h1 className='contactme' >Contact</h1>
      <form onSubmit={handleSubmit(onSubmit)} >
          <input type='text' name='user_name' placeholder='Name' maxLength='30'  />
          <input type='email'  name='email' placeholder='Email' maxLength='30' />
          <textarea name='message' placeholder='Message' maxLength='1500' />
          <p className='message-chars-left'>{messageCharsLeft}</p>
          <input type='submit' value='Send' />
      </form>
    </div>
  )
}

export default Contact
