/// <reference lib="es2015.symbol" />

const SymbolPolyfill: (description?: string) => symbol =
  typeof Symbol === 'function' && typeof Symbol.iterator === 'symbol' ?
    Symbol :
    description => `Symbol(${description})` as any as symbol;

export default SymbolPolyfill;
/// <reference lib="dom" />

export function noop(): undefined {
  return undefined;
}

function getGlobals() {
  if (typeof self !== 'undefined') {
    return self;
  } else if (typeof window !== 'undefined') {
    return window;
  } else if (typeof global !== 'undefined') {
    return global;
  }
  return undefined;
}

export const globals = getGlobals();
import { noop } from '../../utils';
import { AssertionError } from '../../stub/assert';

export function typeIsObject(x: any): x is object {
  return (typeof x === 'object' && x !== null) || typeof x === 'function';
}

export const rethrowAssertionErrorRejection: (e: any) => void =
  DEBUG ? e => {
