export function uponPromise<T>(
  promise: Promise<T>,
  onFulfilled?: (value: T) => void | PromiseLike<void>,
  onRejected?: (reason: any) => void | PromiseLike<void>): void {
  PerformPromiseThen(
    PerformPromiseThen(promise, onFulfilled, onRejected),
    undefined,
    rethrowAssertionErrorRejection
